```javascript
const docs = {
  d1: 'feat, fix, docs, chore, refactor, test',
  d2: 'Logo usage rules.',
  d3: 'Artifact storage path.'
};

function getCommitMessagePrefixes(docs) {
  const commitMessagePrefixes = {};

  for (const docId in docs) {
    if (docs.hasOwnProperty(docId)) {
      const prefix = docs[docId];
      commitMessagePrefixes[docId] = prefix;
